package org.dascy.sovle;/** * @author dongjunzhi * @Title: PrintInOrder * @ProjectName TreadTestDemo * @Description:  给你一个Foo 类，要求使用三个线程，分别调用 first  second third 方法 并按顺序答应 * @create 2024/7/2 16:26 */public class PrintInOrder {    private  static  int state=0;    public static void main(String[] args) {        PrintInOrder printInOrder =new PrintInOrder();        Foo foo = printInOrder.new Foo();        Thread a =new Thread(new Runnable() {            @Override            public void run() {                try {                    synchronized (foo){                        while (state!=0){                            foo.wait();                        }                        foo.first();                        state=1;                        foo.notifyAll();                    }                }catch (InterruptedException e){                    throw new  RuntimeException();                }            }        });        Thread b =new Thread(new Runnable() {            @Override            public void run() {                try {                    synchronized (foo){                        while (state!=1){                            foo.wait();                        }                        foo.second();                        state=2;                        foo.notifyAll();                    }                }catch (InterruptedException e){                    throw new  RuntimeException();                }            }        });        Thread c =new Thread(new Runnable() {            @Override            public void run() {                try {                    synchronized (foo){                        while (state!=2){                            foo.wait();                        }                        foo.third();                        state=0;                        foo.notifyAll();                    }                }catch (InterruptedException e){                    throw new  RuntimeException();                }            }        });        a.start();        b.start();        c.start();    }    public class Foo {        public void first() {            System.out.println("first");        }        public void second() {            System.out.println("second");        }        public void third() {            System.out.println("third");        }    }}